graphql.query:
query {
  planosPorResponsavel(responsavelId: "7583b61b-af1f-4a1f-a506-727e1eda6882") {
    id
    valorTotal
    centroDeCustoId
    cobrancas {
      id
      valor
      dataVencimento
      status
      metodoPagamento
      estaVencida
    }
  }
}

query {
  cobrancas(
    responsavelId: "7583b61b-af1f-4a1f-a506-727e1eda6882", 
    filtro: { status: PENDENTE, vencidaApenas: true }, 
    page: 1, 
    pageSize: 10
  ) {
    items {
      id
      valor
      dataVencimento
      status
    }
    page
    pageSize
  }
}

mutation {
  criarCentroDeCusto(input: { 
    nome: "Taxa de Formatura", 
    descricao: "Terceirao" 
  }) {
    id
    nome
    descricao
  }
}

mutation {
