mutation {
  criarPlano(input: { 
    responsavelId: "ce046ecd-2e54-4594-8cda-93ac32b5b28f", 
    centroDeCustoId: "935245fe-aed1-4f03-b4ef-d6114a932545", 
    cobrancas: [
      { 
        valor: 1500.0, 
        dataVencimento: "2026-12-10T00:00:00Z", 
        metodoPagamento: PIX 
      }
    ] 
  }) {
    id
    valorTotal
    cobrancas {
      id
      valor
    }
  }
}

mutation {
  alterarVencimentoCobranca(
    cobrancaId: "2e49234c-ffbe-477b-ab04-b84af5a39444", 
    input: { 
      novaDataVencimento: "2027-01-15T00:00:00Z" 
    }
  ) {
    id
    dataVencimento
  }
}

mutation {
  alterarMetodoPagamento(
    cobrancaId: "2e49234c-ffbe-477b-ab04-b84af5a39444", 
    input: { 
      novoMetodoPagamento: BOLETO 
    }
  ) {
    id
    metodoPagamento
    codigoPagamento
  }
}
